import UIKit
import RxSwift
import RxCocoa

final class HomeWireframe: BaseWireframe {

    // MARK: - Private properties -

    private let storyboard = UIStoryboard(name: "Home", bundle: nil)

    // MARK: - Module setup -

    init() {
        let moduleViewController = storyboard.instantiateViewController(ofType: HomeViewController.self)
        super.init(viewController: moduleViewController)

        let interactor = HomeInteractor()
        let presenter = HomePresenter(view: moduleViewController, interactor: interactor, wireframe: self)
        moduleViewController.presenter = presenter
    }

}

// MARK: - Extensions -

extension HomeWireframe: HomeWireframeInterface {
    func navigate(to screen: HomeNavigationOption) {
        switch screen {
        case .search(relay: let relay):
            let searchWireframe = SearchWireframe(searchRelay: relay)
            searchWireframe.viewController.modalPresentationStyle = .popover
            viewController.presentWireframe(searchWireframe, animated: true, completion: nil)
        }
    }
    
}
